import LoginForm from "./LoginForm/LoginForm";
import LoginHeader from "./LoginHeader/LoginHeader";
import { useState, useEffect, setState } from 'react';
import axios from "axios";

const url = "http://localhost:4000";

function LoginPanel() {
  const [subtitle, setSubtitle] = useState('zoar com a rapazeada.');
  const email= useState('');
  const [password, setPassword] = useState('');

  function handleSubmit(e) {
    e.preventDefault();
    console.log(e);
    console.log(e.target);
    console.log(email, password);
  }

  function handleEmailChange (email) {
    setState({email: email});
  }


  useEffect(() => {
    axios.get(url + '/welcome')
      .then(response => {
        setSubtitle(response.data.data)
      })
      .catch(error => {
        console.error(error)
      })
  }, []);
  return (
    <div className="container">
        <div className="wrapper">

          <LoginHeader subtitle={subtitle}></LoginHeader>

          <LoginForm handleSubmit={handleSubmit} email={email} setEmail={setEmail} password={password} setPassword={setPassword} />
        </div>
      </div>
  )
}


export default LoginPanel;
